<?phpnamespace App\Http\Controllers\Client\Common\Product;use App\Http\Controllers\Controller;use App\Services\Client\Contracts\GetAllSets;use App\Services\Client\Resources\Set\AllResource;use Illuminate\Http\Request;use Illuminate\Http\Resources\Json\AnonymousResourceCollection;class SetController extends Controller{    public function index(Request $request): AnonymousResourceCollection    {        $response = app(GetAllSets::class)->execute(            $request->header('City')        );        return AllResource::collection($response);    }}