<?phpnamespace App\Http\Controllers\Client\User\Cart;use App\Http\Controllers\Controller;use App\Models\Product;use App\Services\Client\Contracts as Contracts;use App\Services\Client\Resources\Cart as Carts;use App\Models\Cart;use Illuminate\Http\JsonResponse;use Illuminate\Http\Request;use Illuminate\Http\Resources\Json\AnonymousResourceCollection;use Illuminate\Http\Response;class CartController extends Controller{    public function index(): AnonymousResourceCollection    {        $response = app(Contracts\GetAllCarts::class)->execute();        return Carts\AllResource::collection($response);    }    public function store(Product $product, Request $request)    {        app(Contracts\AddProductToCart::class)->execute(            $product, $request->get('quantity'), $request->get('price_id')        );    }    public function destroy(Cart $cart): void    {//        dd(324);        app(Contracts\DestroyProductCart::class)->execute($cart);    }    public function destroyAll():void    {//        dd(213);        app(Contracts\DestroyAllCart::class)->execute();    }}