<?phpnamespace App\Http\Controllers\Client\User\Order;use App\Http\Controllers\Controller;use App\Models\Product;use App\Services\Client\Contracts as Contracts;use App\Services\Client\Requests\Order\ShowRequest;use Illuminate\Http\JsonResponse;class OrderController extends Controller{    public function showProduct(Product $product, ShowRequest $request): JsonResponse    {        $response = app(Contracts\GetMakeOrder::class)            ->execute($product, $request->get('price_id'));        return response()->json($response);    }}