<?phpnamespace App\Http\Controllers\Client\User\Order;use App\Http\Controllers\Controller;use App\Models\Product;use App\Models\User;use App\Services\Client\Contracts as Contracts;use App\Services\Client\Dto\Order\StoreDtoFactory;use App\Services\Client\Requests\Order\ShowRequest;use App\Services\Client\Requests\Order\StoreRequest;use App\Services\Client\Resources\Order\AllResource;use Illuminate\Http\JsonResponse;class OrderController extends Controller{    public function index()    {        $response = app(Contracts\GetAllOrders::class)->execute();        return AllResource::collection($response);    }    public function showProduct(Product $product, ShowRequest $request): JsonResponse    {        $response = app(Contracts\GetMakeOrder::class)            ->execute($product, $request->get('price_id'));        return response()->json($response);    }    public function store(StoreRequest $request): void    {        app(Contracts\StoreOrder::class)            ->execute(StoreDtoFactory::fromRequest($request));    }}