<?phpnamespace App\Repositories\Eloquent;use App\Models\Banner;use App\Repositories\BannerRepositoryInterface;use Illuminate\Database\Eloquent\Builder;use Illuminate\Database\Eloquent\Collection;class BannerRepository extends BaseRepository implements BannerRepositoryInterface{    public function __construct(Banner $model)    {        $this->model = $model;    }    public function getAllByCity(        string $city_id,        array  $columns = ['*'],        array  $relations = [],        array  $relations_count = []    ): Collection    {        return $this->model            ->query()            ->select($columns)            ->where('city_id', $city_id)            ->with($relations)            ->withCount($relations_count)            ->get();    }}