<?phpnamespace App\Repositories\Eloquent;use App\Models\Cart;use App\Repositories\CartRepositoryInterface;use Illuminate\Database\Eloquent\Collection;class CartRepository extends BaseRepository implements CartRepositoryInterface{    public function __construct(Cart $model)    {        return $this->model = $model;    }    public function getAllByClient(        string $client_id,        array  $columns = ['*'],        array  $relations = [],        array  $relations_count = []): Collection    {        return $this->model            ->query()            ->select($columns)            ->where('client_id', $client_id)            ->with($relations)            ->withCount($relations_count)            ->get();    }}