<?phpnamespace App\Repositories\Eloquent;use App\Models\ClientAddress;use App\Repositories\ClientAddressRepositoryInterface;use Illuminate\Database\Eloquent\Collection;class ClientAddressRepository extends BaseRepository implements ClientAddressRepositoryInterface{    public function __construct(ClientAddress $model)    {        $this->model = $model;    }    public function getAllByClient(        string $client_id,        array  $columns = ['*'],        array  $relations = [],        array  $relations_count = []    ): Collection    {        return $this->model            ->query()            ->select($columns)            ->where('client_id', $client_id)            ->with($relations)            ->withCount($relations_count)            ->get();    }}