<?phpnamespace App\Repositories\Eloquent;use App\Models\ClientInvite;use App\Repositories\ClientInviteRepositoryInterface;use Illuminate\Database\Eloquent\Collection;class ClientInviteRepository extends BaseRepository implements ClientInviteRepositoryInterface{    public function __construct(ClientInvite $model)    {        return $this->model = $model;    }    public function getByClient(        string $client_id,        array  $columns = ['*'],        array  $relations = [],        array  $relations_count = [],    ): Collection    {        return $this->model            ->query()            ->select($columns)            ->where('user_id', $client_id)            ->with($relations)            ->withCount($relations_count)            ->latest('id')            ->get();    }}