<?phpnamespace App\Repositories\Eloquent;use App\Models\HelpSection;use App\Repositories\HelpSectionRepositoryInterface;use Illuminate\Database\Eloquent\Collection;class HelpSectionRepository extends BaseRepository implements HelpSectionRepositoryInterface{    public function __construct(HelpSection $model)    {        return $this->model = $model;    }    public function getAll(array $columns = ['*'],                           array $relations = [],                           array $relations_count = []    ): Collection    {        return $this->model            ->query()            ->select($columns)            ->with($relations)            ->withCount($relations_count)            ->get();    }}