<?phpnamespace App\Repositories\Eloquent;use App\Models\Promocode;use App\Repositories\PromocodeRepositoryInterface;class PromocodeRepository extends BaseRepository implements PromocodeRepositoryInterface{    public function __construct(Promocode $model)    {        return $this->model = $model;    }    public function findByCode(string $code): ?Promocode    {        return $this->model            ->query()            ->where('code', $code)            ->first();    }}