<?phpnamespace App\Services\Client\Actions\Banner;use App\Services\Client\Contracts\GetAllBanners;use App\Tasks as Tasks;use Illuminate\Database\Eloquent\Collection;class GetAllAction implements GetAllBanners{    public function execute(string $city_id): Collection    {        return app(Tasks\Banner\GetAllByCityTask::class)->run($city_id,            ['id', 'link', 'position']        );    }}