<?phpnamespace App\Services\Client\Actions\Cart;use App\Models\Client;use App\Services\Client\Contracts\GetAllCarts;use App\Tasks as Tasks;use Auth;use Illuminate\Database\Eloquent\Collection;class GetAllAction implements GetAllCarts{    public function execute(): Collection    {        /** @var Client $client */        $client = Auth::user();        return app(Tasks\Cart\GetAllTask::class)->run(            $client->id,        );    }}