<?phpnamespace App\Services\Client\Actions\ClientAddress;use App\Models\Client;use App\Models\ClientAddress;use App\Services\Client\Contracts\CreateAddress;use App\Services\Client\Dto\ClientAddress\StoreDto;use Auth;class CreateAction implements CreateAddress{    public function execute(StoreDto $dto): void    {        /** @var Client $client */        $client = Auth::user()->client;//        dd(Auth::user()->client);        $this->createAddress($dto, $client);    }    private function createAddress(StoreDto $dto, Client $client): void    {        ClientAddress::create($dto->toArray() + [                'client_id' => $client->id,            ]);    }}