<?phpnamespace App\Services\Client\Actions\ClientAddress;use App\Models\User;use App\Services\Client\Contracts\GetAllAddress;use App\Tasks\ClientAddress\GetAllByClientTask;use Illuminate\Database\Eloquent\Collection;use Auth;class GetAllAction implements GetAllAddress{    public function execute(): Collection    {        /** @var User $client */        $client = Auth::user()->client;        return app(GetAllByClientTask::class)->run($client->id);    }}