<?phpnamespace App\Services\Client\Actions\ClientInvite;use App\Models\Client;use App\Services\Client\Contracts\GetAllClientInvites;use App\Tasks as Tasks;use Auth;use Illuminate\Database\Eloquent\Collection;class GetAllAction implements GetAllClientInvites{    public function execute(): Collection    {        /** @var Client $client */        $client = Auth::user();        return app(Tasks\ClientInvite\GetByClientTask::class)->run(            $client->id,            ['user_id', 'invited_user_id', 'ordered'],            ['invited_user:id,name,phone']        );    }}