<?phpnamespace App\Services\Client\Actions\HelpSection;use App\Models\Setting;use App\Models\HelpSection;use App\Tasks as Tasks;use App\Services\Client\Contracts\GetAllHelpSections;use Illuminate\Support\Collection;class GetAllAction implements GetAllHelpSections{    public function execute(): array    {        $setting = Setting::firstOrFail();        return [            'help-sections' => $this->getHelpSections(),            'contact_telegram' => $setting->contact_telegram,            'contact_whatsapp' => $setting->contact_whatsapp,        ];    }    private    function getHelpSections(): Collection    {        return app(Tasks\HelpSection\GetAllTask::class)            ->run(['id', 'name', 'content'])            ->map(function (HelpSection $section) {                return [                    'id' => $section->id,                    'name' => $section->name,                    'content' => $section->content                ];            });    }}