<?phpnamespace App\Services\Client\Actions\Promocode;use App\Enums\Promocode\Type;use App\Services\Client\Contracts\GetPromocodeValue;use App\Tasks\Promocode\FindPromocodeByCodeTask;use Symfony\Component\HttpKernel\Exception\UnprocessableEntityHttpException;class GetPromocodeAction implements GetPromocodeValue{    public function execute(string $promocode): string    {        $promocode = app(FindPromocodeByCodeTask::class)->run($promocode);        if (empty($promocode)) {            throw new UnprocessableEntityHttpException(                __('Код неверен.')            );        }        return $promocode->amount . ($promocode->type == Type::PERCENT ? '%' : '₸');    }}