<?phpnamespace App\Services\Client\Actions\Set;use App\Services\Client\Contracts\GetAllSets;use App\Tasks as Tasks;use Illuminate\Pagination\Paginator;class GetAllAction implements GetAllSets{    public function execute(string $city_id): Paginator    {        return app(Tasks\Set\GetAllByCityTask::class)->run(            $city_id,            ['products.id', 'products.name'],            ['prices:id,product_id,price,old_price,bonus,days_count']        );    }}