<?phpnamespace App\Services\Client\Dto\ClientAddress;use App\Services\Client\Requests\ClientAddress\StoreRequest;class StoreDtoFactory{    public static function fromRequest(StoreRequest $request): StoreDto    {        return self::fromArray($request->validated());    }    public static function fromArray(array $data): StoreDto    {        return new StoreDto([            'city_id' => $data['city_id'],            'street' => $data['street'],            'building' => $data['building'],            'apartment' => $data['apartment'],        ]);    }}