<?phpnamespace App\Services\Client\Dto\ClientAddress;use App\Services\Client\Requests\ClientAddress\UpdateRequest;class UpdateDtoFactory{    public static function fromRequest(UpdateRequest $request): UpdateDto    {        return self::fromArray($request->validated());    }    public static function fromArray(array $data): UpdateDto    {        return new UpdateDto([            'city_id' => $data['city_id'],            'street' => $data['street'],            'building' => $data['building'],            'apartment' => $data['apartment'],        ]);    }}