<?phpnamespace App\Services\Client\Dto\Order;use App\Services\Client\Requests\Order\StoreRequest;class StoreDtoFactory{    public static function fromRequest(StoreRequest $request): StoreDto    {        return self::fromArray($request->validated());    }    public static function fromArray(array $data): StoreDto    {        return new StoreDto([            'products' => $data['products'],            'address_id' => $data['address_id'],            'spend_bonuses' => $data['spend_bonuses'],            'promocode' => $data['promocode'] ?? null,        ]);    }}