<?phpnamespace App\Services\Client\Requests\Login;use Illuminate\Foundation\Http\FormRequest;class VerifyCodeRequest extends FormRequest{    public function authorize()    {        return auth()->guest();    }    public function rules()    {        return [            'phone' => 'required|phone',            'verification_code' => 'required|string',            'device_token' => 'nullable|string',        ];    }    public function messages(): array    {        return [            'phone.exists' => 'number phone doesnt have exists',        ];    }}