<?phpnamespace App\Services\Client\Requests\Order;use Illuminate\Foundation\Http\FormRequest;class StoreRequest extends FormRequest{    public function authorize()    {        return auth()->check();    }    public function rules()    {        return [            'products' => 'required|array|min:1',            'products.*.product_id' => 'required|integer',            'products.*.price_id' => 'required|integer',            'products.*.quantity' => 'required|integer|min:1',            'address_id' => 'required|numeric|',            'spend_bonuses' => 'required|boolean',            'promocode' => 'nullable|string',        ];    }}