<?phpnamespace App\Services\Client\Requests\Registration;use Illuminate\Foundation\Http\FormRequest;class VerifyCodeRequest extends FormRequest{    public function authorize(): bool    {        return auth()->guest();    }    public function rules(): array    {        return [            'phone' => 'required|string|phone',            'verification_code' => 'required|string|max:4',            'device_token' => 'nullable|string|max:1000',        ];    }}