<?phpnamespace App\Services\Client\Resources\Banner;use App\Models\Banner;use Illuminate\Http\Request;use Illuminate\Http\Resources\Json\JsonResource;class AllResource extends JsonResource{    public function toArray(Request $request)    {        /** @var Banner $this */        return [            'id' => $this->id,            'link' => $this->link,            'image' => $this->getImage(),        ];    }}