<?phpnamespace App\Services\Client\Resources\Cart;use App\Enums\File\Type;use App\Models\Cart;use App\Models\ProductPrice;use Illuminate\Http\Request;use Illuminate\Http\Resources\Json\JsonResource;class AllResource extends JsonResource{    public function toArray(Request $request): array    {        /** @var Cart $resource */        $cart = $this->resource;        $product = $cart->product;        $price = ProductPrice::where('product_id', $product->id)->find($cart->price_id);        return [            'id' => $cart->id,            'product' => [                'id' => $product->id,                'name' => $product->name,                'cover' => $product->getMedia(Type::COVER),            ],            'price' => [                'days_count' => $price->days_count,                'price' => $price->price,            ],        ];    }}