<?phpnamespace App\Services\Client\Resources\ClientAddress;use App\Models\ClientAddress;use Illuminate\Http\Request;use Illuminate\Http\Resources\Json\JsonResource;class EditResource extends JsonResource{    public function toArray(Request $request)    {        /** @var ClientAddress $this */        return [            'id' => $this->id,            'city_id' => $this->city_id,            'street' => $this->street,            'building' => $this->building,            'apartment' => $this->apartment,        ];    }}