<?phpnamespace App\Services\Client\Resources\ClientInvite;use App\Models\ClientInvite;use Illuminate\Http\Request;use Illuminate\Http\Resources\Json\JsonResource;class AllResource extends JsonResource{    public function toArray(Request $request)    {        /** @var ClientInvite $this */        return [            'name' => $this->invited_user->name,            'phone' => $this->invited_user->phone,            'is_activated' => (bool)$this->ordered,        ];    }}