<?phpnamespace App\Services\Client\Resources\Game;use App\Enums\File\Type;use App\Models\File;use App\Models\Product;use Illuminate\Http\Request;use Illuminate\Http\Resources\Json\JsonResource;class ShowResource extends JsonResource{    public function toArray(Request $request)    {        /** @var Product $this */        return [            'id' => $this->id,            'name' => $this->name,            'cover' => $this->getMedia(Type::COVER),            'description' => $this->description,            'images' => $this->filterMediasByType(Type::PHOTO)->map(function (File $file) {                return [                    'id' => $file->id,                    'type' => $file->type,                    'path' => $file->getFile(),                ];            }),        ];    }}