<?phpnamespace App\Services\Client\Resources\Profile;use App\Models\Client;use App\Models\User;use Illuminate\Http\Resources\Json\JsonResource;class ShowResource extends JsonResource{    public function toArray($request): array    {        /** @var User $client */        $client = $this->resource;        return [            'id' => $client->id,            'name' => $client->name,            'surname' => $client->surname,            'phone' => $client->phone,            'bonus' => $client->client->bonus_amount,        ];    }}