<?phpnamespace App\Services\Client\Resources\Set;use App\Enums\File\Type;use App\Models\Product;use Illuminate\Http\Request;use Illuminate\Http\Resources\Json\JsonResource;class AllResource extends JsonResource{    public function toArray(Request $request)    {        /** @var Product $set */        $set = $this->resource;        $prices = $set->prices;        return [            'product' => [                'id' => $set->id,                'name' => $set->name,                'image' => $set->getMedia(Type::COVER),            ],            'prices' => $prices,        ];    }}