<?phpnamespace App\Services\Client\Resources\Settings;use App\Models\Client;use App\Models\Setting;use Auth;use Illuminate\Http\Resources\Json\JsonResource;class ShowResource extends JsonResource{    public function toArray($request): array    {        /** @var Setting $setting */        $setting = $this->resource;        /** @var Client $client */        $client = Auth::user()->client;        return [            'bonus' => [                'id' => $setting->id,                'register_count' => $setting->register_count,                'register_amount' => $setting->register_amount,                'order_count' => $setting->order_count,                'order_amount' => $setting->order_amount,            ],            'client_promo_code' => $client->promo_code,        ];    }}