<?phpnamespace App\SubActions\Client;use App\Helpers\NumberHelper;use App\Models\User;class CreateOrUpdateVerificationSubAction{    public function run(User $client, string $description): string    {        $code = NumberHelper::getRandomNumber(4);        if ($client->current_verification()->exists()) {            $client->current_verification()->update(['code' => $code]);        } else {            $client->verifications()->create([                'code' => $code,                'description' => $description,                'created_by' => auth()->check() ? auth()->id() : $client->id,                'verifiable_type' => User::class,                'verifiable_id' => $client->id,            ]);        }        return $code;    }}