<?phpnamespace App\Tasks\Banner;use App\Repositories\BannerRepositoryInterface;use Illuminate\Database\Eloquent\Collection;class GetAllByCityTask{    public function __construct(private readonly BannerRepositoryInterface $repository){}    public function run(        string $city_id,        array $columns = ['*'],        array $relations = [],        array $relations_count = []    ): Collection    {        return $this->repository->getAllByCity(            $city_id, $columns, $relations, $relations_count        );    }}