<?phpnamespace App\Tasks\Cart;use App\Repositories\CartRepositoryInterface;use Illuminate\Database\Eloquent\Collection;class GetAllTask{    public function __construct(private readonly CartRepositoryInterface $repository)    {    }    public function run(        string $client_id,        array  $columns = ['*'],        array  $relations = [],        array  $relations_count = []): Collection    {        return $this->repository->getAllByClient(            $client_id,            $columns,            $relations,            $relations_count);    }}