<?phpnamespace App\Tasks\ClientAddress;use App\Repositories\ClientAddressRepositoryInterface;class GetAllByClientTask{    public function __construct(private readonly ClientAddressRepositoryInterface $repository)    {    }    public function run(string $client_id,                        array  $columns = ['*'],                        array  $relations = [],                        array  $relations_count = [])    {        return $this->repository->getAllByClient($client_id, $columns, $relations, $relations_count);    }}