<?phpnamespace App\Tasks\ClientInvite;use App\Repositories\ClientInviteRepositoryInterface;class GetByClientTask{    public function __construct(private readonly ClientInviteRepositoryInterface $repository)    {    }    public function run(        string $client_id,        array  $columns = ['*'],        array  $relations = [],        array  $relations_count = [])    {        return $this->repository->getByClient($client_id,            $columns, $relations, $relations_count);    }}