<?phpnamespace App\Tasks\HelpSection;use App\Repositories\HelpSectionRepositoryInterface;class GetAllTask{    public function __construct(private readonly HelpSectionRepositoryInterface $repository)    {    }    public function run(array $columns = ['*'],                        array $relations = [],                        array $relations_count = [])    {        return $this->repository->getAll($columns, $relations, $relations_count);    }}