<?phpnamespace App\Tasks\Set;use App\Repositories\ProductRepositoryInterface;use Illuminate\Contracts\Pagination\Paginator;class GetAllByCityTask{    public function __construct(private readonly ProductRepositoryInterface $repository){}    public function run(        string $city_id,        array $columns = ['*'],        array $relations = [],        array $relations_count = []    ): Paginator    {        return $this->repository->getAllSetsByCity($city_id, $columns, $relations, $relations_count);    }}