<?phpnamespace App\Tasks\User;use App\Models\User;use App\Repositories\UserRepInterface;class FindByEmail{    public function __construct(private readonly UserRepInterface $repository)    {    }    public function run(string $email,                        array  $columns = ['*'],                        array  $relations = [],                        array  $relations_count = []) :?User    {        return $this->repository->findByEmail($email, $columns, $relations, $relations_count);    }}