<?phpuse App\Http\Controllers\Client\User\Address as Address;use Illuminate\Support\Facades\Route;Route::prefix('addresses')->group(function () {    Route::get('', [Address\AddressController::class, 'index']);    Route::post('', [Address\AddressController::class, 'store']);    Route::prefix('{address}')->group(function () {        Route::get('', [Address\AddressController::class, 'edit']);        Route::put('', [Address\AddressController::class, 'update']);        Route::delete('', [Address\AddressController::class, 'destroy']);    });});