<?phpuse App\Http\Controllers\Client\User\Cart as Cart;Route::group(['prefix' => 'carts'], function () {    Route::get('', [Cart\CartController::class, 'index']);    Route::delete('', [Cart\CartController::class, 'destroyAll']);    Route::group(['prefix' => '{cart}'], function () {        Route::delete('', [Cart\CartController::class, 'destroy']);    });    Route::group(['prefix' => 'products'], function () {        Route::group(['prefix' => '{product}'], function () {            Route::post('', [Cart\CartController::class, 'store']);        });    });});