<?phpuse App\Http\Controllers\Client\User\Cart\CartController;use Illuminate\Support\Facades\Route;Route::prefix('carts')->group(function () {    Route::get('', [CartController::class, 'index']);    Route::delete('', [CartController::class, 'destroyAll']);    Route::prefix('{cart}')->group(function () {        Route::delete('', [CartController::class, 'destroy']);    });    Route::prefix('products')->group(function () {        Route::prefix('{product}')->group(function () {            Route::post('', [CartController::class, 'store']);        });    });});